import React from "react";
import { useNavigate } from "react-router-dom";
import { MARKET_MAIN_ROUTE, SERVICES_ROUTE } from "../../utils/consts";

import "./ServicesItem.css";


const ServicesItem = (props) => {
    const navigate = useNavigate();

    const navService = (e) => {
        const headerOfService = e.querySelector("h4");
        console.log(headerOfService.innerHTML);

        if (headerOfService === "Маркет") {
            navigate(MARKET_MAIN_ROUTE);
        } else {
            navigate(SERVICES_ROUTE);
        }
    }

    return (
        <div className="service--item" onClick={navService}>
            <img src={props.img} alt="" />
            <span>
                <h4>{props.header}</h4>
                <p>{props.desc}</p>
            </span>
        </div>
    );
}

export default ServicesItem;
